import React, {useState} from "react";
import './Homework.css';

function Homework() {
    return (
    <div className="Homework">
        <h1>Homework</h1>
        <BasicButton default={0} />
    </div>
    );
}

function BasicButton(props){
    // var numberState = useState(props.default);
    // var number = numberState[0];
    // var setNumber = numberState[1];
    var [number, setNumber] = useState(props.default);
    
    return(
        <div className="Homework">
            <p>Number : {number}</p>
            //이 버튼들 쪼개는게 가능할까??? 
            <input id="plus" type="button" value="+1" onClick={() => setNumber(number+=1)}/>
            <input id="default" type="button" value="reset" onClick={() => setNumber(0)}/>
            <input id="minus" type="button" value="-1" onClick={() => setNumber(number-=1)}/>
        </div>
    );
}

export default Homework;
